import React, { useState } from 'react';

// const [count, setCount] = useState(0);

export default function TextForm() {
    const [text, setText] = useState('');
    const textHandle = (elem) => {
        setText( elem.target.value)
        // console.log("onchange")

    }
    const convertUpCase = () => {
        setText(text.toLocaleUpperCase())

    }
  return (
    <>
      <div className="container">
        <div className="form-group">
          <label htmlFor="text">Enter your Text</label>
          <textarea className="form-control" id="text" rows="8" value={text} onChange = {textHandle}></textarea>
        </div>
          </div>
          <div className="container">
              <button className="btn btn-primary" onClick={convertUpCase}>Convert into UpperCase</button>
          </div>
    </>
  );
}
