import './App.css';
import React, { useState } from 'react';
import Navbar from './components/Navbar';
import TextForm from './components/TextForm';
import Alertmsg from './components/Alertmsg';
import About from './components/About';
// import { Switch } from "react-router-dom";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link
} from "react-router-dom";

function App() {
  const [mode, setMode] = useState('light');
  const [alert, setAlert] = useState(null);

  const showAlert = (message,type) => {
    setAlert({
      msg : message,
      type : type
    })
    setTimeout(() => {
      setAlert(null)
    }, 1000);
  }

  const changeMode = () => {
    if (mode === 'light') {
      setMode('dark')
      document.body.style.backgroundColor = '#0b0632';
      document.body.style.color = 'white';
      showAlert("Dark mode has been active","success")
    }
    else {
      setMode('light');
      document.body.style.backgroundColor = 'white';
      document.body.style.color = '#0b0632';
      showAlert("Light mode has been active","success")
    }
  }

  return (
    <Router>
      <div className="app-container">
        <Navbar title="TextUtils" mode1={mode} change_Mode={changeMode} />
        <Routes>
          <Route path='/' element={<TextForm mode1={mode} showAlert={showAlert} />} />

          <Route path='/' element={<About mode1={mode} />} />
        </Routes>

      </div>
    </Router>

    // <Router>
    //   <div>
    //     <Navbar title="TextUtils" mode1={mode} change_Mode={changeMode} />
    //     <Alertmsg alert={alert} />
    //     {/* <About mode1={mode} />
    //     <TextForm mode1={mode} showAlert={showAlert} /> */}
    //   <Switch>
    //       <Route path="/">
    //         <TextForm mode1={mode} showAlert={showAlert} />
    //       </Route>
    //       <Route path="/about">
    //         <About mode1={mode} />
    //       </Route>
    //   </Switch>

    //     </div>
    //     </Router>

  );
}

export default App;
