import './App.css';
import React, { useState } from 'react';
import Navbar from './components/Navbar';
import TextForm from './components/TextForm';
import Alertmsg from './components/Alertmsg';

function App() {
  const [mode, setMode] = useState('light');
  const [message, setMessage] = useState(null);
  const [type, setType] = useState(null);


  const changeMode = () => {
    if (mode === 'light') {
      setMode('dark')
      document.body.style.backgroundColor = '#0b0632';
      document.body.style.color = 'white';
      setMessage("active dark mode")
      setType("success")
    }
    else {
      setMode('light');
      document.body.style.backgroundColor = 'white';
      document.body.style.color = '#0b0632';
      setMessage("active light mode")
      setType("success")

    }
  }

  return (
    <>
      <Navbar title="TextUtils" mode1={mode} change_Mode={changeMode} />
      <Alertmsg msg={message} type={type} />
      <TextForm mode1={mode}/>
    </>
  );
}

export default App;
