import React, { useState } from "react";

// const [count, setCount] = useState(0);

export default function TextForm(props) {
  const [text, setText] = useState("");
  const textHandle = (elem) => {
    setText(elem.target.value);
    // console.log("onchange")
  };
  const convertUpCase = () => {
    setText(text.toUpperCase());
  };
  const convertLoCase = () => {
    setText(text.toLowerCase());
  };
  return (
    <>
      <div
        className={`container my-5}`}
      >
        <div className="form-group">
          <label htmlFor="text">Enter your Text</label>
          <textarea
            className="form-control"
            id="text"
            rows="8"
            value={text}
            onChange={textHandle}
          ></textarea>
        </div>
      </div>
      <div className="container">
        <button className="btn btn-primary mx-1" onClick={convertUpCase}>
          Convert into UpperCase
        </button>
        <button className="btn btn-primary mx-1" onClick={convertLoCase}>
          Convert into LowerCase
        </button>
      </div>
      <div className="container my-3">
        <p>
          {text === "" ? "0" : text.split(" ").length} word and {text.length}{" "}
          character.
        </p>
        <h2>Preview</h2>
        <p>
          {text === ""
            ? "plz write text in above box to see the preview"
            : text}
        </p>
      </div>
    </>
  );
}
